select departamento.nombre, persona.apellido1, persona.apellido2, persona.nombre from universidad.departamento departamento left join universidad.profesor profesor on profesor.id_departamento = departamento.id left join universidad.persona persona on persona.id = profesor.id_profesor order by departamento.nombre, persona.apellido1 asc, persona.apellido2 asc, persona.nombre asc; 
select * from universidad.persona persona left join universidad.profesor profesor on profesor.id_profesor = persona.id where persona.tipo = 'profesor' and profesor.id_profesor is null;
select * from universidad.persona persona left join universidad.profesor profesor on profesor.id_profesor = persona.id where persona.tipo = 'profesor' and profesor.id_profesor is null;
select * from universidad.persona persona left join universidad.profesor profesor on profesor.id_profesor = persona.id where persona.tipo = 'profesor' and profesor.id_profesor is null;
select * from universidad.asignatura asignatura left join universidad.profesor profesor on profesor.id_profesor = asignatura.id_profesor where asignatura.id_profesor is null;
select distinct departamento.* from universidad.departamento departamento left join universidad.profesor profesor on departamento.id = profesor.id_departamento left join universidad.asignatura asignatura on profesor.id_profesor = asignatura.id_profesor where asignatura.id_profesor is null;